// Process webhook data for multi-user support
console.log('=== PRODUCTION WEBHOOK DEBUG ===');

// Try to get data from webhook
let data = {};
let headers = {};

try {
  const input = $input.first().json;
  console.log('Raw input:', JSON.stringify(input, null, 2));

  if (input.body) {
    data = typeof input.body === 'string' ? JSON.parse(input.body) : input.body;
  } else {
    data = input;
  }

  headers = input.headers || {};

} catch (e) {
  console.log('Error parsing webhook data:', e.message);
  data = $json || {};
}

console.log('Parsed data:', JSON.stringify(data, null, 2));

// Extract user ID from multiple sources
let userId = data.user_id || data.userId || headers['x-user-id'] || 'd33d28ea-cc43-4dd0-b971-e896acf853e3';

// Handle different request types
let platform = data.platform || (data.platforms && data.platforms[0]) || 'twitter';
let content = data.content || 'Test post from production workflow! ðŸš€';

console.log(`ðŸŽ¯ Processing request for user ${userId} on platform ${platform}`);

return {
  userId: userId,
  platform: platform.toLowerCase(),
  content: content,
  postId: data.postId || data.post_id || `post-${Date.now()}`,
  timestamp: new Date().toISOString()
};

// Merge webhook data with OAuth credentials
const webhookData = $node['Process Webhook Data'].json;
const credentials = $json;

console.log('Webhook data:', JSON.stringify(webhookData, null, 2));
console.log('Credentials found:', !!credentials?.access_token);

if (!credentials || !credentials.access_token) {
  throw new Error(`No OAuth credentials found for user ${webhookData.userId} on platform ${webhookData.platform}. Please connect your ${webhookData.platform} account first.`);
}

return {
  userId: webhookData.userId,
  platform: webhookData.platform,
  content: webhookData.content,
  postId: webhookData.postId,
  access_token: credentials.access_token,
  platform_user_id: credentials.platform_user_id
};

// Create success response
const webhookData = $node['Process Webhook Data'].json;
const platformResponse = $json;

let platformPostId = 'unknown';
if (platformResponse?.data?.id) {
  platformPostId = platformResponse.data.id;
} else if (platformResponse?.id) {
  platformPostId = platformResponse.id;
}

return {
  success: true,
  message: `Successfully posted to ${webhookData.platform}`,
  platform: webhookData.platform,
  postId: webhookData.postId,
  userId: webhookData.userId,
  content: webhookData.content,
  platformPostId: platformPostId,
  timestamp: new Date().toISOString()
};
