// Process and validate pending posts
const posts = Array.isArray($input.first().json) ? $input.first().json : [];
const validPosts = [];

console.log(`ðŸ“‹ Processing ${posts.length} scheduled posts`);

for (const post of posts) {
  // Validate required fields
  if (!post.queue_id || !post.post_id || !post.content) {
    console.warn(`âš ï¸ Skipping invalid post: missing required fields`);
    continue;
  }

  const postData = {
    queueId: post.queue_id,
    postId: post.post_id,
    userId: post.user_id,
    content: post.content,
    platforms: Array.isArray(post.platforms) ? post.platforms : [],
    mediaUrls: Array.isArray(post.media_urls) ? post.media_urls : [],
    scheduledFor: post.scheduled_for,
    oauthCredentials: post.oauth_credentials || {}
  };

  // Validate platforms
  if (postData.platforms.length === 0) {
    console.warn(`âš ï¸ Skipping post ${postData.postId}: no platforms specified`);
    continue;
  }

  console.log(`ðŸ“ Post ${postData.postId}: "${postData.content.substring(0, 50)}..."`);
  console.log(`ðŸ“± Platforms: ${postData.platforms.join(', ')}`);
  console.log(`ðŸ‘¤ User: ${postData.userId}`);

  validPosts.push(postData);
}

console.log(`âœ… ${validPosts.length} valid posts ready for processing`);

return validPosts.map(item => ({ json: item }));

// Complete Social Media Publishing System
const postData = $input.first().json;
const platforms = postData.platforms || [];
const content = postData.content || '';
const mediaUrls = postData.mediaUrls || [];
const oauthCredentials = postData.oauthCredentials || {};
const queueId = postData.queueId;
const postId = postData.postId;
const userId = postData.userId;

console.log(`ðŸš€ Publishing post ${postId} to ${platforms.length} platforms`);
console.log(`ðŸ“ Content: ${content.substring(0, 100)}${content.length > 100 ? '...' : ''}`);

const results = [];
const platformPostIds = {};
let successCount = 0;
let failureCount = 0;

// Configuration
const RATE_LIMIT_DELAY = 3000; // 3 seconds between platforms
const MAX_RETRIES = 2;
const REQUEST_TIMEOUT = 30000; // 30 seconds

// Process each platform
for (const platform of platforms) {
  let retryCount = 0;
  let success = false;

  while (retryCount < MAX_RETRIES && !success) {
    try {
      console.log(`ðŸ“¤ Publishing to ${platform} (attempt ${retryCount + 1}/${MAX_RETRIES})...`);

      const credentials = oauthCredentials[platform];
      if (!credentials || !credentials.access_token) {
        throw new Error(`No OAuth credentials found for ${platform}`);
      }

      // Check token expiration
      if (credentials.expires_at) {
        const expiryDate = new Date(credentials.expires_at);
        const now = new Date();
        const timeUntilExpiry = expiryDate.getTime() - now.getTime();

        if (timeUntilExpiry <= 0) {
          throw new Error(`Access token expired for ${platform} (expired: ${expiryDate.toISOString()})`);
        }

        if (timeUntilExpiry < 300000) { // 5 minutes
          console.warn(`âš ï¸ Token for ${platform} expires soon: ${expiryDate.toISOString()}`);
        }
      }

      let postResult;
      switch (platform.toLowerCase()) {
        case 'twitter':
          postResult = await publishToTwitter(content, mediaUrls, credentials);
          break;
        case 'facebook':
          postResult = await publishToFacebook(content, mediaUrls, credentials);
          break;
        case 'instagram':
          postResult = await publishToInstagram(content, mediaUrls, credentials);
          break;
        case 'linkedin':
          postResult = await publishToLinkedIn(content, mediaUrls, credentials);
          break;
        case 'reddit':
          postResult = await publishToReddit(content, mediaUrls, credentials);
          break;
        default:
          throw new Error(`Unsupported platform: ${platform}`);
      }

      // Success
      results.push({
        platform,
        success: true,
        postId: postResult.id,
        url: postResult.url,
        attempts: retryCount + 1,
        timestamp: new Date().toISOString()
      });

      platformPostIds[platform] = postResult.id;
      successCount++;
      success = true;

      console.log(`âœ… ${platform}: ${postResult.url}`);

    } catch (error) {
      retryCount++;
      const errorMsg = error.message || 'Unknown error';
      console.error(`âŒ ${platform} attempt ${retryCount} failed: ${errorMsg}`);

      if (retryCount >= MAX_RETRIES) {
        results.push({
          platform,
          success: false,
          error: errorMsg,
          attempts: retryCount,
          timestamp: new Date().toISOString()
        });
        failureCount++;
      } else {
        // Exponential backoff for retries
        const delay = 1000 * Math.pow(2, retryCount);
        console.log(`â³ Waiting ${delay}ms before retry...`);
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  }

  // Rate limiting between platforms
  const platformIndex = platforms.indexOf(platform);
  if (platformIndex < platforms.length - 1) {
    console.log(`â³ Rate limiting: waiting ${RATE_LIMIT_DELAY}ms before next platform...`);
    await new Promise(resolve => setTimeout(resolve, RATE_LIMIT_DELAY));
  }
}

// Platform-specific publishing functions
async function publishToTwitter(content, mediaUrls, credentials) {
  try {
    // Twitter character limit
    const tweetText = content.length > 280 ? content.substring(0, 277) + '...' : content;

    const requestBody = { text: tweetText };

    // Note: Media upload would require additional implementation
    if (mediaUrls && mediaUrls.length > 0) {
      console.log(`ðŸ“Ž Twitter: ${mediaUrls.length} media files detected (media upload not implemented)`);
    }

    const response = await $http.request({
      method: 'POST',
      url: 'https://api.twitter.com/2/tweets',
      headers: {
        'Authorization': `Bearer ${credentials.access_token}`,
        'Content-Type': 'application/json'
      },
      body: requestBody,
      timeout: REQUEST_TIMEOUT
    });

    if (!response.data || !response.data.id) {
      throw new Error('Invalid response from Twitter API: missing tweet ID');
    }

    return { 
      id: response.data.id, 
      url: `https://twitter.com/user/status/${response.data.id}` 
    };
  } catch (error) {
    if (error.response) {
      const status = error.response.status;
      const data = error.response.data;
      throw new Error(`Twitter API error (${status}): ${JSON.stringify(data)}`);
    }
    throw new Error(`Twitter error: ${error.message}`);
  }
}

async function publishToFacebook(content, mediaUrls, credentials) {
  try {
    const postData = {
      message: content,
      access_token: credentials.access_token
    };

    // Add link preview for media
    if (mediaUrls && mediaUrls.length > 0) {
      console.log(`ðŸ“Ž Facebook: Adding media as link preview`);
      postData.link = mediaUrls[0];
    }

    const response = await $http.request({
      method: 'POST',
      url: 'https://graph.facebook.com/me/feed',
      headers: {
        'Content-Type': 'application/json'
      },
      body: postData,
      timeout: REQUEST_TIMEOUT
    });

    if (!response.id) {
      throw new Error('Invalid response from Facebook API: missing post ID');
    }

    return { 
      id: response.id, 
      url: `https://facebook.com/${response.id}` 
    };
  } catch (error) {
    if (error.response) {
      const status = error.response.status;
      const data = error.response.data;
      throw new Error(`Facebook API error (${status}): ${JSON.stringify(data)}`);
    }
    throw new Error(`Facebook error: ${error.message}`);
  }
}

async function publishToInstagram(content, mediaUrls, credentials) {
  try {
    if (!mediaUrls || mediaUrls.length === 0) {
      throw new Error('Instagram posts require at least one media file');
    }

    console.log(`ðŸ“Ž Instagram: Creating media container for ${mediaUrls[0]}`);

    // Step 1: Create media container
    const mediaData = {
      image_url: mediaUrls[0],
      caption: content,
      access_token: credentials.access_token
    };

    const mediaResponse = await $http.request({
      method: 'POST',
      url: 'https://graph.instagram.com/me/media',
      headers: {
        'Content-Type': 'application/json'
      },
      body: mediaData,
      timeout: REQUEST_TIMEOUT
    });

    if (!mediaResponse.id) {
      throw new Error('Failed to create Instagram media container');
    }

    console.log(`ðŸ“Ž Instagram: Publishing media container ${mediaResponse.id}`);

    // Step 2: Publish media
    const publishData = {
      creation_id: mediaResponse.id,
      access_token: credentials.access_token
    };

    const publishResponse = await $http.request({
      method: 'POST',
      url: 'https://graph.instagram.com/me/media_publish',
      headers: {
        'Content-Type': 'application/json'
      },
      body: publishData,
      timeout: REQUEST_TIMEOUT
    });

    if (!publishResponse.id) {
      throw new Error('Failed to publish Instagram media');
    }

    return { 
      id: publishResponse.id, 
      url: `https://instagram.com/p/${publishResponse.id}` 
    };
  } catch (error) {
    if (error.response) {
      const status = error.response.status;
      const data = error.response.data;
      throw new Error(`Instagram API error (${status}): ${JSON.stringify(data)}`);
    }
    throw new Error(`Instagram error: ${error.message}`);
  }
}

async function publishToLinkedIn(content, mediaUrls, credentials) {
  try {
    console.log(`ðŸ“Ž LinkedIn: Getting user profile...`);

    // Get LinkedIn person ID
    const profileResponse = await $http.request({
      method: 'GET',
      url: 'https://api.linkedin.com/v2/people/(id:me)',
      headers: {
        'Authorization': `Bearer ${credentials.access_token}`,
        'Content-Type': 'application/json'
      },
      timeout: REQUEST_TIMEOUT
    });

    if (!profileResponse.id) {
      throw new Error('Failed to get LinkedIn profile ID');
    }

    const postData = {
      author: `urn:li:person:${profileResponse.id}`,
      lifecycleState: 'PUBLISHED',
      specificContent: {
        'com.linkedin.ugc.ShareContent': {
          shareCommentary: {
            text: content
          },
          shareMediaCategory: mediaUrls && mediaUrls.length > 0 ? 'ARTICLE' : 'NONE'
        }
      },
      visibility: {
        'com.linkedin.ugc.MemberNetworkVisibility': 'PUBLIC'
      }
    };

    // Add media if present
    if (mediaUrls && mediaUrls.length > 0) {
      console.log(`ðŸ“Ž LinkedIn: Adding media reference`);
      postData.specificContent['com.linkedin.ugc.ShareContent'].media = [{
        status: 'READY',
        originalUrl: mediaUrls[0]
      }];
    }

    const response = await $http.request({
      method: 'POST',
      url: 'https://api.linkedin.com/v2/ugcPosts',
      headers: {
        'Authorization': `Bearer ${credentials.access_token}`,
        'Content-Type': 'application/json'
      },
      body: postData,
      timeout: REQUEST_TIMEOUT
    });

    if (!response.id) {
      throw new Error('Invalid response from LinkedIn API: missing post ID');
    }

    return { 
      id: response.id, 
      url: `https://linkedin.com/feed/update/${response.id}` 
    };
  } catch (error) {
    if (error.response) {
      const status = error.response.status;
      const data = error.response.data;
      throw new Error(`LinkedIn API error (${status}): ${JSON.stringify(data)}`);
    }
    throw new Error(`LinkedIn error: ${error.message}`);
  }
}

async function publishToReddit(content, mediaUrls, credentials) {
  try {
    // Note: Subreddit should be configurable per user
    const subreddit = 'test'; // TODO: Make this dynamic

    const title = content.length > 100 ? content.substring(0, 97) + '...' : content;

    const postData = {
      api_type: 'json',
      kind: mediaUrls && mediaUrls.length > 0 ? 'link' : 'self',
      sr: subreddit,
      title: title,
      text: content
    };

    if (mediaUrls && mediaUrls.length > 0) {
      console.log(`ðŸ“Ž Reddit: Posting as link with media`);
      postData.url = mediaUrls[0];
      delete postData.text;
    }

    const response = await $http.request({
      method: 'POST',
      url: 'https://oauth.reddit.com/api/submit',
      headers: {
        'Authorization': `Bearer ${credentials.access_token}`,
        'Content-Type': 'application/x-www-form-urlencoded',
        'User-Agent': 'SocialMediaScheduler/1.0 by YourApp'
      },
      body: new URLSearchParams(postData),
      timeout: REQUEST_TIMEOUT
    });

    if (!response.json || !response.json.data || !response.json.data.id) {
      throw new Error('Invalid response from Reddit API: missing post data');
    }

    return { 
      id: response.json.data.id, 
      url: response.json.data.url 
    };
  } catch (error) {
    if (error.response) {
      const status = error.response.status;
      const data = error.response.data;
      throw new Error(`Reddit API error (${status}): ${JSON.stringify(data)}`);
    }
    throw new Error(`Reddit error: ${error.message}`);
  }
}

// Calculate final status
const finalStatus = failureCount === 0 ? 'completed' : 
                   successCount === 0 ? 'failed' : 'partial';

const errorMessage = failureCount > 0 ? 
  results.filter(r => !r.success).map(r => `${r.platform}: ${r.error}`).join('; ') : null;

// Return comprehensive results
const summary = {
  queueId,
  postId,
  userId,
  results,
  platformPostIds,
  successCount,
  failureCount,
  totalPlatforms: platforms.length,
  status: finalStatus,
  errorMessage,
  timestamp: new Date().toISOString(),
  executionId: `${$workflow.id}-${$execution.id}`
};

console.log(`ðŸ“Š Publishing Summary:`);
console.log(`   Success: ${successCount}/${platforms.length} platforms`);
console.log(`   Status: ${finalStatus}`);
if (errorMessage) {
  console.log(`   Errors: ${errorMessage}`);
}

return [summary];

// Log execution summary and send notifications
const data = $input.first().json;

console.log('ðŸŽ‰ EXECUTION COMPLETED!');
console.log('=' .repeat(50));
console.log(`ðŸ“ Post ID: ${data.postId}`);
console.log(`ðŸ‘¤ User ID: ${data.userId}`);
console.log(`ðŸ“Š Results: ${data.successCount}/${data.totalPlatforms} platforms successful`);
console.log(`ðŸ“ˆ Status: ${data.status.toUpperCase()}`);
console.log(`â° Timestamp: ${data.timestamp}`);
console.log(`ðŸ”§ Execution: ${data.executionId}`);

if (data.results && data.results.length > 0) {
  console.log('\nðŸ“± Platform Results:');
  data.results.forEach(result => {
    if (result.success) {
      console.log(`   âœ… ${result.platform}: ${result.url} (${result.attempts} attempts)`);
    } else {
      console.log(`   âŒ ${result.platform}: ${result.error} (${result.attempts} attempts)`);
    }
  });
}

if (data.errorMessage) {
  console.log(`\nðŸš¨ Errors: ${data.errorMessage}`);
}

console.log('=' .repeat(50));

// Prepare summary for potential webhook/notification
const executionSummary = {
  postId: data.postId,
  userId: data.userId,
  status: data.status,
  successCount: data.successCount,
  failureCount: data.failureCount,
  totalPlatforms: data.totalPlatforms,
  platformPostIds: data.platformPostIds,
  errorMessage: data.errorMessage,
  timestamp: data.timestamp,
  executionId: data.executionId,
  results: data.results
};

return [{ 
  executionSummary,
  success: data.status === 'completed',
  hasErrors: data.failureCount > 0,
  message: data.status === 'completed' ? 
    `Successfully published to ${data.successCount} platforms` :
    data.status === 'failed' ?
    `Failed to publish to all platforms` :
    `Published to ${data.successCount}/${data.totalPlatforms} platforms`
}];
