// Process webhook data for scheduling
const webhookData = $input.first().json;
const body = webhookData.body || {};

console.log('ðŸ“¨ Webhook received - scheduling post:', JSON.stringify(body, null, 2));

// Validate required fields
if (!body.user_id || !body.content) {
  throw new Error('Missing required fields: user_id and content are required');
}

// Extract platforms
let platforms = [];
if (body.platforms && Array.isArray(body.platforms)) {
  platforms = body.platforms;
} else if (body.platform) {
  platforms = [body.platform];
} else {
  throw new Error('No platform specified');
}

// Process scheduled time
let scheduledFor = new Date();
if (body.scheduled_for) {
  scheduledFor = new Date(body.scheduled_for);
  console.log('ðŸ“… Post scheduled for:', scheduledFor.toISOString());
} else {
  // If no time specified, schedule for immediate posting (1 minute from now)
  scheduledFor = new Date(Date.now() + 60000);
  console.log('âš¡ No schedule time - posting immediately in 1 minute');
}

// Structure the data
const processedData = {
  post_id: body.post_id || `webhook-${Date.now()}`,
  user_id: body.user_id,
  content: body.content,
  platforms: platforms,
  media_urls: body.media_urls || [],
  scheduled_for: scheduledFor.toISOString(),
  action: 'schedule_post',
  webhook_received: true
};

console.log('âœ… Processed for scheduling:', processedData);

return { json: processedData };

// Process each pending post
const posts = $input.all();
const results = [];

console.log(`ðŸ“‹ Processing ${posts.length} pending posts for auto-posting`);

for (const post of posts) {
  const postData = {
    queue_id: post.json.id,
    post_id: post.json.post_id,
    user_id: post.json.user_id,
    content: post.json.content,
    platforms: JSON.parse(post.json.platforms || '[]'),
    media_urls: JSON.parse(post.json.media_urls || '[]'),
    scheduled_for: post.json.scheduled_for,
    oauth_credentials: post.json.oauth_credentials || {}
  };

  console.log(`ðŸ“ Auto-posting: "${postData.content.substring(0, 50)}..." to ${postData.platforms.join(', ')}`);

  results.push(postData);
}

return results.map(item => ({ json: item }));

// Auto-publish to social media platforms
const postData = $input.first().json;
const platforms = postData.platforms || [];
const content = postData.content || '';
const mediaUrls = postData.media_urls || [];
const oauthCredentials = postData.oauth_credentials || {};
const queueId = postData.queue_id;
const postId = postData.post_id;

console.log(`ðŸš€ Auto-publishing post ${postId} to ${platforms.length} platforms`);

const results = [];
const platformPostIds = {};
let successCount = 0;
let failureCount = 0;

for (const platform of platforms) {
  try {
    console.log(`ðŸ“¤ Publishing to ${platform}...`);

    const credentials = oauthCredentials[platform];
    if (!credentials || !credentials.access_token) {
      throw new Error(`No OAuth credentials for ${platform}`);
    }

    let postResult;
    switch (platform.toLowerCase()) {
      case 'twitter':
        postResult = await publishToTwitter(content, mediaUrls, credentials);
        break;
      case 'facebook':
        postResult = await publishToFacebook(content, mediaUrls, credentials);
        break;
      case 'instagram':
        postResult = await publishToInstagram(content, mediaUrls, credentials);
        break;
      case 'linkedin':
        postResult = await publishToLinkedIn(content, mediaUrls, credentials);
        break;
      case 'reddit':
        postResult = await publishToReddit(content, mediaUrls, credentials);
        break;
      default:
        // For testing - simulate successful posting
        postResult = {
          id: `${platform}_${Date.now()}`,
          url: `https://${platform}.com/post/${Date.now()}`
        };
        console.log(`âœ… ${platform}: Simulated post created`);
    }

    results.push({
      platform,
      success: true,
      postId: postResult.id,
      url: postResult.url
    });

    platformPostIds[platform] = postResult.id;
    successCount++;

    console.log(`âœ… ${platform}: ${postResult.url}`);

  } catch (error) {
    console.error(`âŒ ${platform} failed: ${error.message}`);
    results.push({
      platform,
      success: false,
      error: error.message
    });
    failureCount++;
  }
}

// Platform publishing functions (simplified for demo)
async function publishToTwitter(content, mediaUrls, credentials) {
  console.log('ðŸ¦ Posting to Twitter:', content.substring(0, 50));
  return { 
    id: `tw_${Date.now()}`, 
    url: `https://twitter.com/user/status/${Date.now()}` 
  };
}

async function publishToFacebook(content, mediaUrls, credentials) {
  console.log('ðŸ“˜ Posting to Facebook:', content.substring(0, 50));
  return { 
    id: `fb_${Date.now()}`, 
    url: `https://facebook.com/${Date.now()}` 
  };
}

async function publishToInstagram(content, mediaUrls, credentials) {
  console.log('ðŸ“· Posting to Instagram:', content.substring(0, 50));
  return { 
    id: `ig_${Date.now()}`, 
    url: `https://instagram.com/p/${Date.now()}` 
  };
}

async function publishToLinkedIn(content, mediaUrls, credentials) {
  console.log('ðŸ’¼ Posting to LinkedIn:', content.substring(0, 50));
  return { 
    id: `li_${Date.now()}`, 
    url: `https://linkedin.com/feed/update/${Date.now()}` 
  };
}

async function publishToReddit(content, mediaUrls, credentials) {
  console.log('ðŸ¤– Posting to Reddit:', content.substring(0, 50));
  return { 
    id: `rd_${Date.now()}`, 
    url: `https://reddit.com/r/test/comments/${Date.now()}` 
  };
}

// Return results
const finalStatus = failureCount === 0 ? 'completed' : 
                   successCount === 0 ? 'failed' : 'partial';

const summary = {
  queue_id: queueId,
  post_id: postId,
  results,
  platformPostIds,
  successCount,
  failureCount,
  totalPlatforms: platforms.length,
  status: finalStatus,
  errorMessage: failureCount > 0 ? 
    results.filter(r => !r.success).map(r => `${r.platform}: ${r.error}`).join('; ') : null
};

console.log(`ðŸ“Š Auto-post complete: ${successCount}/${platforms.length} successful, status: ${finalStatus}`);

return { json: summary };
