// Process each post individually for better error isolation
const posts = $input.all();
const results = [];

console.log(`ðŸ“‹ Processing ${posts.length} scheduled posts`);

for (const post of posts) {
  const postData = {
    queueId: post.json.queue_id,
    postId: post.json.post_id,
    userId: post.json.user_id,
    content: post.json.content,
    platforms: post.json.platforms || [],
    mediaUrls: post.json.media_urls || [],
    scheduledFor: post.json.scheduled_for,
    oauthCredentials: post.json.oauth_credentials || {}
  };

  console.log(`ðŸ“ Post ${postData.postId}: "${postData.content.substring(0, 50)}..."`);
  console.log(`ðŸ“± Platforms: ${postData.platforms.join(', ')}`);

  results.push(postData);
}

return results.map(item => ({ json: item }));

// Enhanced Social Media Publishing with Production Features
const postData = $input.first().json;
const platforms = postData.platforms || [];
const content = postData.content || '';
const mediaUrls = postData.mediaUrls || [];
const oauthCredentials = postData.oauthCredentials || {};
const queueId = postData.queueId;
const postId = postData.postId;

console.log(`ðŸš€ Publishing post ${postId} to ${platforms.length} platforms`);

const results = [];
const platformPostIds = {};
let successCount = 0;
let failureCount = 0;

// Rate limiting configuration
const RATE_LIMIT_DELAY = 2000; // 2 seconds between platforms
const MAX_RETRIES = 3;

for (const platform of platforms) {
  let retryCount = 0;
  let success = false;

  while (retryCount < MAX_RETRIES && !success) {
    try {
      console.log(`ðŸ“¤ Publishing to ${platform} (attempt ${retryCount + 1})...`);

      const credentials = oauthCredentials[platform];
      if (!credentials || !credentials.access_token) {
        throw new Error(`No OAuth credentials for ${platform}`);
      }

      // Check token expiration
      if (credentials.expires_at) {
        const expiryDate = new Date(credentials.expires_at);
        if (expiryDate <= new Date()) {
          throw new Error(`Access token expired for ${platform}`);
        }
      }

      let postResult;
      switch (platform.toLowerCase()) {
        case 'twitter':
          postResult = await publishToTwitter(content, mediaUrls, credentials);
          break;
        case 'facebook':
          postResult = await publishToFacebook(content, mediaUrls, credentials);
          break;
        case 'instagram':
          postResult = await publishToInstagram(content, mediaUrls, credentials);
          break;
        case 'linkedin':
          postResult = await publishToLinkedIn(content, mediaUrls, credentials);
          break;
        case 'reddit':
          postResult = await publishToReddit(content, mediaUrls, credentials);
          break;
        default:
          throw new Error(`Unsupported platform: ${platform}`);
      }

      results.push({
        platform,
        success: true,
        postId: postResult.id,
        url: postResult.url,
        attempts: retryCount + 1
      });

      platformPostIds[platform] = postResult.id;
      successCount++;
      success = true;

      console.log(`âœ… ${platform}: ${postResult.url}`);

    } catch (error) {
      retryCount++;
      console.error(`âŒ ${platform} attempt ${retryCount} failed: ${error.message}`);

      if (retryCount >= MAX_RETRIES) {
        results.push({
          platform,
          success: false,
          error: error.message,
          attempts: retryCount
        });
        failureCount++;
      } else {
        // Wait before retry
        await new Promise(resolve => setTimeout(resolve, 1000 * retryCount));
      }
    }
  }

  // Rate limiting between platforms
  if (platforms.indexOf(platform) < platforms.length - 1) {
    await new Promise(resolve => setTimeout(resolve, RATE_LIMIT_DELAY));
  }
}

// Enhanced platform publishing functions
async function publishToTwitter(content, mediaUrls, credentials) {
  const tweetText = content.length > 280 ? content.substring(0, 277) + '...' : content;

  const response = await $http.request({
    method: 'POST',
    url: 'https://api.twitter.com/2/tweets',
    headers: {
      'Authorization': `Bearer ${credentials.access_token}`,
      'Content-Type': 'application/json'
    },
    body: { text: tweetText }
  });

  if (!response.data?.id) {
    throw new Error('Invalid Twitter API response');
  }

  return { 
    id: response.data.id, 
    url: `https://twitter.com/user/status/${response.data.id}` 
  };
}

async function publishToFacebook(content, mediaUrls, credentials) {
  const postData = {
    message: content,
    access_token: credentials.access_token
  };

  if (mediaUrls?.length > 0) {
    postData.link = mediaUrls[0];
  }

  const response = await $http.request({
    method: 'POST',
    url: 'https://graph.facebook.com/me/feed',
    body: postData
  });

  if (!response.id) {
    throw new Error('Invalid Facebook API response');
  }

  return { 
    id: response.id, 
    url: `https://facebook.com/${response.id}` 
  };
}

async function publishToInstagram(content, mediaUrls, credentials) {
  if (!mediaUrls?.length) {
    throw new Error('Instagram requires media');
  }

  // Create media container
  const mediaResponse = await $http.request({
    method: 'POST',
    url: 'https://graph.instagram.com/me/media',
    body: {
      image_url: mediaUrls[0],
      caption: content,
      access_token: credentials.access_token
    }
  });

  // Publish media
  const publishResponse = await $http.request({
    method: 'POST',
    url: 'https://graph.instagram.com/me/media_publish',
    body: {
      creation_id: mediaResponse.id,
      access_token: credentials.access_token
    }
  });

  return { 
    id: publishResponse.id, 
    url: `https://instagram.com/p/${publishResponse.id}` 
  };
}

async function publishToLinkedIn(content, mediaUrls, credentials) {
  // Get person ID
  const profileResponse = await $http.request({
    method: 'GET',
    url: 'https://api.linkedin.com/v2/people/(id:me)',
    headers: {
      'Authorization': `Bearer ${credentials.access_token}`
    }
  });

  const postData = {
    author: `urn:li:person:${profileResponse.id}`,
    lifecycleState: 'PUBLISHED',
    specificContent: {
      'com.linkedin.ugc.ShareContent': {
        shareCommentary: { text: content },
        shareMediaCategory: 'NONE'
      }
    },
    visibility: {
      'com.linkedin.ugc.MemberNetworkVisibility': 'PUBLIC'
    }
  };

  const response = await $http.request({
    method: 'POST',
    url: 'https://api.linkedin.com/v2/ugcPosts',
    headers: {
      'Authorization': `Bearer ${credentials.access_token}`,
      'Content-Type': 'application/json'
    },
    body: postData
  });

  return { 
    id: response.id, 
    url: `https://linkedin.com/feed/update/${response.id}` 
  };
}

async function publishToReddit(content, mediaUrls, credentials) {
  const postData = {
    api_type: 'json',
    kind: 'self',
    sr: 'test', // Configure this
    title: content.substring(0, 100),
    text: content
  };

  const response = await $http.request({
    method: 'POST',
    url: 'https://oauth.reddit.com/api/submit',
    headers: {
      'Authorization': `Bearer ${credentials.access_token}`,
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    body: new URLSearchParams(postData)
  });

  return { 
    id: response.json.data.id, 
    url: response.json.data.url 
  };
}

// Return comprehensive results
const finalStatus = failureCount === 0 ? 'completed' : 
                   successCount === 0 ? 'failed' : 'partial';

const summary = {
  queueId,
  postId,
  results,
  platformPostIds,
  successCount,
  failureCount,
  totalPlatforms: platforms.length,
  status: finalStatus,
  errorMessage: failureCount > 0 ? 
    results.filter(r => !r.success).map(r => `${r.platform}: ${r.error}`).join('; ') : null
};

console.log(`ðŸ“Š Final: ${successCount}/${platforms.length} successful, status: ${finalStatus}`);

return [summary];
